import React from 'react';


export default class App extends React.Component{
	constructor(props){
		super(props);
		this.state = {

			display: '0',
			flag: false,
			functional: false
		}

	}

	addToDisplay = (event) => {


		let numberAdd;

		if(this.state.flag && !(['+','-','*','/'].some((val) => val == event.target.value))){
			numberAdd = event.target.value;

		}

		else if(this.state.display == '0' && ['+','-','*','/'].every((val) => val != event.target.value)){
			numberAdd = event.target.value;

		} else {
			numberAdd = this.state.display + event.target.value;
		}

		this.setState({display: numberAdd, flag: false});

	}




	finishOperation = (event) => {
		let toUtil = this.state.display;
		const len = toUtil.length;
		for(let i = 0; i < len; i++){ 
			toUtil = toUtil.replace('++','+').replace('//','/').replace('***','**').replace('--','-').replace('..','.')
				.replace('+-','+').replace('+*','+').replace('+/','+')
				.replace('-+','-').replace('-*','-').replace('-/','-')
				.replace('*+','*').replace('*-','*').replace('*/','*')
				.replace('/+','/').replace('/-','/').replace('/*','/')
				.replace('.+','.').replace('.-','.').replace('.*','.').replace('./','.');		
		}

		if(toUtil == '.'){

			toUtil = '0';
		}

		if(['/','+','*','-'].some((el) => el == toUtil[toUtil.length - 1])){

			toUtil = toUtil.split('');
			toUtil.pop();
			toUtil - toUtil.join('');


		}



		this.setState({display: eval(`${toUtil}`), flag: true})

	}






	render(){

		return (

			<div>
				<div>{this.state.display}</div>
				<div><button onClick = {() => this.setState({display: '0'})}> C </button><button onClick = {() => this.setState({display: '0'})}> CA </button> <button> F </button><button onClick = {this.addToDisplay} value = '+'> + </button></div> 
				<div><button onClick = {this.addToDisplay} value = '7'> 7 </button> <button onClick = {this.addToDisplay} value = '8'> 8 </button> <button onClick = {this.addToDisplay} value = '9'> 9 </button><button onClick = {this.addToDisplay} value = '-'> - </button></div>
				<div><button onClick = {this.addToDisplay} value = '4'> 4 </button> <button onClick = {this.addToDisplay} value = '5'> 5 </button> <button onClick = {this.addToDisplay} value = '6'> 6 </button><button onClick = {this.addToDisplay} value = '*'> * </button></div>
				<div><button onClick = {this.addToDisplay} value = '1'> 1 </button> <button onClick = {this.addToDisplay} value = '2'> 2 </button> <button onClick = {this.addToDisplay} value = '3'> 3 </button><button onClick = {this.addToDisplay} value = '/'> / </button></div>
				<div><button onClick = {this.addToDisplay} value = '0'> 0 </button> <button onClick = {this.addToDisplay} value = '.'> . </button> <button onClick = {this.finishOperation}> = </button></div>
			</div>	

		)

	}


}
