import React from 'react';


export default class App extends React.Component{
	constructor(props){
		super(props);
		this.state = {

			display: '0',

		}

	}

	addToDisplay = (event) => {
		this.setState({display: this.state.display + event.target.value})	



	}







	render(){

		return (

			<div>
				{this.state.display}
				<button onClick = {this.addToDisplay} value = '1'> 1 </button>
			</div>		
		)

	}


}
