import React from 'react';


export default class App extends React.Component{
	constructor(props){
		super(props);
		this.state = {

			display: '0',

		}

	}

	addToDisplay = (event) => {


		let numberAdd;

		if(this.state.display == '0'){
			numberAdd = event.target.value;

		} else {
			numberAdd = this.state.display + event.target.value;
		}

		this.setState({display: numberAdd});




	}







	render(){

		return (

			<div>
				{this.state.display}
				<button onClick = {this.addToDisplay} value = '7'> 7 </button> <button onClick = {this.addToDisplay} value = '8'> 8 </button> <button onClick = {this.addToDisplay} value = '9'> 9 </button>
				<button onClick = {this.addToDisplay} value = '4'> 4 </button> <button onClick = {this.addToDisplay} value = '5'> 5 </button> <button onClick = {this.addToDisplay} value = '6'> 6 </button>
				<button onClick = {this.addToDisplay} value = '1'> 1 </button> <button onClick = {this.addToDisplay} value = '2'> 2 </button> <button onClick = {this.addToDisplay} value = '3'> 3 </button> 
				<button onClick = {this.addToDisplay} value = '0'> 0 </button> <button onClick = {this.addToDisplay} value = ','> , </button> <button onClick = {this.addToDisplay} value = '1'> 1 </button> 
			</div>	

		)

	}


}
