import React from 'react';


export default class App extends React.Component{
	constructor(props){
		super(props);
		this.state = {

			display: '0',

		}

	}

	addToDisplay = (event) => {


		let numberAdd;

		if(this.state.display == '0'){
			numberAdd = event.target.value;

		} else {
			numberAdd = this.state.display + event.target.value;
		}

		this.setState({display: numberAdd});




	}







	render(){

		return (

			<div>
				{this.state.display}
				<button onClick = {this.addToDisplay} value = '1'> 1 </button>
			</div>		
		)

	}


}
