import React from 'react';


export default class App extends React.Component{
	constructor(props){
		super(props);
		this.state = {

			display: '0',
			flag: false,	
		}

	}

	addToDisplay = (event) => {


		let numberAdd;

		if(this.state.flag){
			this.setState({display: '0'})

		}

		if(this.state.display == '0' && ['+','-','*','/'].every((val) => val != event.target.value)){
			numberAdd = event.target.value;

		} else {
			numberAdd = this.state.display + event.target.value;
		}

		this.setState({display: numberAdd});	
	}

	finishOperation = (event) => {

		this.setState({display: eval(this.state.display), flag: true})

	}






	render(){

		return (

			<div>
				{this.state.display}
				<div><button onClick = {this.addToDisplay} value = '7'> 7 </button> <button onClick = {this.addToDisplay} value = '8'> 8 </button> <button onClick = {this.addToDisplay} value = '9'> 9 </button><button onClick = {this.addToDisplay} value = '+'> + </button></div>
				<div><button onClick = {this.addToDisplay} value = '4'> 4 </button> <button onClick = {this.addToDisplay} value = '5'> 5 </button> <button onClick = {this.addToDisplay} value = '6'> 6 </button></div>
				<div><button onClick = {this.addToDisplay} value = '1'> 1 </button> <button onClick = {this.addToDisplay} value = '2'> 2 </button> <button onClick = {this.addToDisplay} value = '3'> 3 </button></div>
				<div><button onClick = {this.addToDisplay} value = '0'> 0 </button> <button onClick = {this.addToDisplay} value = '.'> . </button> <button onClick = {this.finishOperation}> = </button> </div>
			</div>	

		)

	}


}
